$.fn.easy_accordion=function(b,a){if(!b){b={plusText:"more info",lessText:"less info",startHidden:true}}if(!a){a=".easy_accordion"}$(a).each(function(){b.plusText=(b.plusText==undefined)?"+":b.plusText;b.lessText=(b.lessText==undefined)?"-":b.lessText;b.startHidden=(b.startHidden==undefined)?true:b.startHidden;var d=this.href.split("#").length;var c="#"+this.href.split("#")[d-1];if(b.startHidden){$(c).css("display","none")}$(this).click(function(){$(c).slideToggle("slow");if($(this).text()==b.plusText){$(this).text(b.lessText)}else{$(this).text(b.plusText)}return false})})};